import { Meta, StoryObj, moduleMetadata } from '@storybook/angular';
import { DsDrawerComponent } from './ds-drawer';
import { DsButtonComponent } from '../button/ds-button';
import { NgpDialogTrigger, NgpDialogOverlay, NgpDialog } from 'ng-primitives/dialog';

interface DrawerStoryProps {
  position: 'left' | 'right';
}

const meta: Meta<DrawerStoryProps> = {
  title: 'Primitives/Drawer',
  component: DsDrawerComponent,
  tags: ['autodocs'],
  decorators: [
    moduleMetadata({
      imports: [DsDrawerComponent, DsButtonComponent, NgpDialogTrigger, NgpDialogOverlay, NgpDialog],
    }),
  ],
  argTypes: {
    position: {
      control: 'select',
      options: ['left', 'right'],
      description: 'Position of the drawer',
      defaultValue: 'right'
    }
  }
};

export default meta;
type Story = StoryObj<DrawerStoryProps>;

const Template: Story['render'] = (args) => ({
  props: args,
  template: `
    <div style="padding: 20px;">
      <ds-button [ngpDialogTrigger]="drawer" variant="primary">Open Drawer</ds-button>
      <p>This is the main page content. When you open the drawer, it should overlay this content with a semi-transparent background.</p>
    </div>

    <ng-template #drawer let-close="close">
      <div ngpDialogOverlay class="ds-drawer-overlay">
        <ds-drawer ngpDialog [position]="position">
          <h2 slot="header" class="tw-text-xl tw-font-semibold">Drawer Title</h2>
          <div slot="content">
            <p class="tw-text-neutral-700 tw-mb-4">This is the main content of the drawer.</p>
            <p class="tw-text-neutral-600 tw-mb-4">You can put any content here including forms, lists, or other components.</p>
            <div class="tw-p-4 tw-bg-gray-50 tw-rounded-lg">
              <h3 class="tw-font-medium tw-mb-2">Example Content</h3>
              <p class="tw-text-sm tw-text-gray-600">This drawer should slide in from the {{ position }} side of the screen.</p>
            </div>
          </div>
          <div slot="footer">
            <ds-button variant="ghost" (click)="close()">Cancel</ds-button>
            <ds-button variant="primary" (click)="close()">Save Changes</ds-button>
          </div>
        </ds-drawer>
      </div>
    </ng-template>
  `
});

export const RightDrawer: Story = {
  args: {
    position: 'right'
  },
  render: Template
};

export const LeftDrawer: Story = {
  args: {
    position: 'left'
  },
  render: Template
};

import { Meta, StoryObj, moduleMetadata, componentWrapperDecorator } from '@storybook/angular';
import { FormsModule } from '@angular/forms';
import { DsSelectComponent, DsSelectOption } from './ds-select';

interface ExampleOption {
  id: string;
  name: string;
}

const meta: Meta<DsSelectComponent> = {
  title: 'Primitives/Select',
  component: DsSelectComponent,
  decorators: [
    moduleMetadata({
      imports: [FormsModule],
    }),
    componentWrapperDecorator(
      (story) => `<div style="display: flex; justify-content: center;"><div style="width: 200px;">${story}</div></div>`
    )
  ],
  tags: ['autodocs'],
  parameters: {
    layout: 'centered',
    docs: {
      story: {
        inline: true,
        iframeHeight: 100,
      }
    }
  },
  argTypes: {
    variant: {
      control: 'select',
      options: ['default', 'error', 'warning', 'success'],
    },
    disabled: {
      control: 'boolean',
    },
    required: {
      control: 'boolean',
    },
    ghost: {
      control: 'boolean',
    },
  },
};

export default meta;
type Story = StoryObj<DsSelectComponent>;

const simpleOptions: DsSelectOption[] = [
  { id: '1', label: 'Option 1', value: 1 },
  { id: '2', label: 'Option 2', value: 2 },
  { id: '3', label: 'Option 3', value: 3 },
  { id: '4', label: 'Option 4', value: 4 },
];

const groupedOptions: DsSelectOption[] = [
  { id: '1', label: 'Apple', value: 'apple', group: 'Fruits' },
  { id: '2', label: 'Banana', value: 'banana', group: 'Fruits' },
  { id: '3', label: 'Orange', value: 'orange', group: 'Fruits' },
  { id: '4', label: 'Carrot', value: 'carrot', group: 'Vegetables' },
  { id: '5', label: 'Broccoli', value: 'broccoli', group: 'Vegetables' },
  { id: '6', label: 'Potato', value: 'potato', group: 'Vegetables' },
];

export const Default: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    variant: 'default',
    disabled: false,
    required: false,
    ghost: false,
  },
};

export const WithGroups: Story = {
  args: {
    options: groupedOptions,
    placeholder: 'Select food',
    variant: 'default',
  },
};

export const Disabled: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    disabled: true,
  },
};

export const Error: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    variant: 'error',
  },
};

export const Warning: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    variant: 'warning',
  },
};

export const Success: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    variant: 'success',
  },
};

export const WithDisabledOptions: Story = {
  args: {
    options: [
      { id: '1', label: 'Option 1', value: 1 },
      { id: '2', label: 'Option 2', value: 2, disabled: true },
      { id: '3', label: 'Option 3', value: 3 },
      { id: '4', label: 'Option 4', value: 4, disabled: true },
    ],
    placeholder: 'Select an option',
  },
};

export const Ghost: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Ghost select',
    ghost: true,
  },
};

export const GhostWithGroups: Story = {
  args: {
    options: groupedOptions,
    placeholder: 'Ghost select with groups',
    ghost: true,
  },
};

import { Meta, StoryObj, applicationConfig } from '@storybook/angular';
import { signal } from '@angular/core';
import { DsEditableTableComponent, type DsEditableTableColumnMeta } from './ds-editable-table';
import { editableTextCell, editableNumberCell, editableSelectCell } from './editable-cell-helpers';
import { provideIcons } from '@ng-icons/core';
import { 
  remixAddLine,
  remixDeleteBinLine,
  remixDraggable,
  remixInboxLine,
  remixArrowUpLine,
  remixArrowDownLine,
} from '@ng-icons/remixicon';
import type { ColumnDef } from '@tanstack/angular-table';

// Sample data types
interface InvoiceLine {
  id: string;
  name: string;
  description: string;
  quantity: number;
  unitPrice: number;
  discount: number;
  total: number;
}

interface Product {
  id: string;
  name: string;
  sku: string;
  category: string;
  price: number;
  stock: number;
}

interface Task {
  id: string;
  title: string;
  assignee: string;
  priority: string;
  status: string;
  dueDate: string;
}

// Sample data
const sampleInvoiceLines: InvoiceLine[] = [
  { 
    id: '1', 
    name: 'Administration fee', 
    description: 'Covers handling and coordination', 
    quantity: 1, 
    unitPrice: 500, 
    discount: 0, 
    total: 500 
  },
  { 
    id: '2', 
    name: 'Miscellaneous', 
    description: 'Various small items', 
    quantity: 2, 
    unitPrice: 400, 
    discount: 0, 
    total: 800 
  },
  { 
    id: '3', 
    name: 'Technical Manager', 
    description: 'Consulting services', 
    quantity: 6.5, 
    unitPrice: 800, 
    discount: 0, 
    total: 5200 
  },
];

const sampleProducts: Product[] = [
  { id: '1', name: 'Laptop Pro', sku: 'LT-001', category: 'Electronics', price: 1299.99, stock: 45 },
  { id: '2', name: 'Wireless Mouse', sku: 'MS-102', category: 'Accessories', price: 29.99, stock: 150 },
  { id: '3', name: 'USB-C Cable', sku: 'CB-203', category: 'Accessories', price: 12.99, stock: 8 },
];

const sampleTasks: Task[] = [
  { id: '1', title: 'Fix ventilation system', assignee: 'John Doe', priority: 'High', status: 'In Progress', dueDate: '2024-01-15' },
  { id: '2', title: 'Repaint hallway', assignee: 'Jane Smith', priority: 'Medium', status: 'To Do', dueDate: '2024-01-20' },
  { id: '3', title: 'Replace light fixtures', assignee: 'Bob Johnson', priority: 'Low', status: 'Done', dueDate: '2024-01-10' },
];

// Priority options for select
const priorityOptions = [
  { id: 'low', label: 'Low', value: 'Low' },
  { id: 'medium', label: 'Medium', value: 'Medium' },
  { id: 'high', label: 'High', value: 'High' },
];

// Status options for select
const statusOptions = [
  { id: 'todo', label: 'To Do', value: 'To Do' },
  { id: 'inprogress', label: 'In Progress', value: 'In Progress' },
  { id: 'done', label: 'Done', value: 'Done' },
];

// Category options for select
const categoryOptions = [
  { id: 'electronics', label: 'Electronics', value: 'Electronics' },
  { id: 'accessories', label: 'Accessories', value: 'Accessories' },
  { id: 'furniture', label: 'Furniture', value: 'Furniture' },
  { id: 'office', label: 'Office', value: 'Office' },
];

// Column definitions
const invoiceColumns: ColumnDef<InvoiceLine>[] = [
  {
    accessorKey: 'name',
    header: 'Name',
    cell: (info) => editableTextCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      placeholder: 'Enter name...'
    }),
    meta: {
      sizing: {
        minWidth: 'md',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'description',
    header: 'Description',
    cell: (info) => editableTextCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      placeholder: 'Add description...'
    }),
    meta: {
      sizing: {
        minWidth: 'lg',
        maxWidth: 'xl',
        truncate: true,
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'quantity',
    header: 'Quantity',
    cell: (info) => editableNumberCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      min: 0,
      step: 0.5,
    }),
    meta: {
      sizing: {
        maxWidth: 'xs',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'unitPrice',
    header: 'Unit Price',
    cell: (info) => editableNumberCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      min: 0,
      step: 0.01,
    }),
    meta: {
      sizing: {
        maxWidth: 'sm',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'discount',
    header: 'Discount',
    cell: (info) => editableNumberCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      min: 0,
    }),
    meta: {
      sizing: {
        maxWidth: 'sm',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'total',
    header: 'Total',
    cell: (info) => info.getValue(),
    enableSorting: true,
    meta: {
      sizing: {
        maxWidth: 'sm',
      }
    } as DsEditableTableColumnMeta,
  },
];

const productColumns: ColumnDef<Product>[] = [
  {
    accessorKey: 'name',
    header: 'Product Name',
    cell: (info) => editableTextCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      placeholder: 'Enter product name...'
    }),
    meta: {
      sizing: {
        minWidth: 'md',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'sku',
    header: 'SKU',
    cell: (info) => editableTextCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      placeholder: 'SKU-000'
    }),
    meta: {
      sizing: {
        maxWidth: 'sm',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'category',
    header: 'Category',
    cell: (info) => editableSelectCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      options: categoryOptions,
    }),
    meta: {
      sizing: {
        maxWidth: 'md',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'price',
    header: 'Price',
    cell: (info) => editableNumberCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      min: 0,
      step: 0.01,
    }),
    meta: {
      sizing: {
        maxWidth: 'sm',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'stock',
    header: 'Stock',
    cell: (info) => editableNumberCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      min: 0,
      step: 1,
    }),
    meta: {
      sizing: {
        maxWidth: 'xs',
      }
    } as DsEditableTableColumnMeta,
  },
];

const taskColumns: ColumnDef<Task>[] = [
  {
    accessorKey: 'title',
    header: 'Task',
    cell: (info) => editableTextCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      placeholder: 'Enter task title...'
    }),
    meta: {
      sizing: {
        minWidth: 'lg',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'assignee',
    header: 'Assignee',
    cell: (info) => editableTextCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      placeholder: 'Assign to...'
    }),
    meta: {
      sizing: {
        minWidth: 'md',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'priority',
    header: 'Priority',
    cell: (info) => editableSelectCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      options: priorityOptions,
    }),
    meta: {
      sizing: {
        maxWidth: 'sm',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'status',
    header: 'Status',
    cell: (info) => editableSelectCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
      options: statusOptions,
    }),
    meta: {
      sizing: {
        maxWidth: 'md',
      }
    } as DsEditableTableColumnMeta,
  },
  {
    accessorKey: 'dueDate',
    header: 'Due Date',
    cell: (info) => editableTextCell({
      row: info.row.original,
      rowIndex: info.row.index,
      value: info.getValue(),
    }),
    meta: {
      sizing: {
        maxWidth: 'md',
      }
    } as DsEditableTableColumnMeta,
  },
];

const meta: Meta<DsEditableTableComponent> = {
  title: 'Components/Editable Table',
  component: DsEditableTableComponent,
  decorators: [
    applicationConfig({
      providers: [
        provideIcons({
          remixAddLine,
          remixDeleteBinLine,
          remixDraggable,
          remixInboxLine,
          remixArrowUpLine,
          remixArrowDownLine,
        }),
      ],
    }),
  ],
  tags: ['autodocs'],
  parameters: {
    docs: {
      description: {
        component: 'An editable data table component with inline editing, drag-and-drop row reordering, and row management. When `reorderable` is true, sorting is disabled. When false, column sorting is enabled.',
      },
    },
  },
  argTypes: {
    data: {
      control: false,
      description: 'Array of data to display and edit (two-way binding)',
    },
    columns: {
      control: false,
      description: 'Column definitions for the table',
    },
    reorderable: {
      control: 'boolean',
      description: 'Enable drag-and-drop row reordering (disables sorting when true)',
    },
    allowAddRow: {
      control: 'boolean',
      description: 'Allow adding new rows',
    },
    allowDeleteRow: {
      control: 'boolean',
      description: 'Allow deleting rows',
    },
    showDragHandle: {
      control: 'boolean',
      description: 'Show drag handle column (only when reorderable is true)',
    },
    emptyMessage: {
      control: 'text',
      description: 'Message to display when table is empty',
    },
    addRowButtonText: {
      control: 'text',
      description: 'Text for add row button',
    },
  },
};

export default meta;
type Story = StoryObj<DsEditableTableComponent>;

export const InvoiceLinesReorderable: Story = {
  render: (args) => ({
    props: {
      ...args,
      invoiceData: signal([...sampleInvoiceLines]),
      newRowTemplate: {
        id: Math.random().toString(36).substr(2, 9),
        name: '',
        description: 'Add description',
        quantity: 1,
        unitPrice: 0,
        discount: 0,
        total: 0
      },
      onCellEdited: (event: any) => {
        console.log('Cell edited:', event);
      },
      onRowAdded: (row: any) => {
        console.log('Row added:', row);
      },
      onRowDeleted: (event: any) => {
        console.log('Row deleted:', event);
      },
      onRowReordered: (event: any) => {
        console.log('Row reordered:', event);
      },
    },
    template: `
      <ds-editable-table
        [(data)]="invoiceData"
        [columns]="columns"
        [reorderable]="true"
        [allowAddRow]="true"
        [allowDeleteRow]="true"
        [showDragHandle]="true"
        [addRowButtonText]="'Add invoice line'"
        [newRowTemplate]="newRowTemplate"
        (cellEdited)="onCellEdited($event)"
        (rowAdded)="onRowAdded($event)"
        (rowDeleted)="onRowDeleted($event)"
        (rowReordered)="onRowReordered($event)"
      />
    `,
  }),
  args: {
    columns: invoiceColumns,
  },
  parameters: {
    docs: {
      description: {
        story: 'Invoice lines table with drag-and-drop reordering enabled. Sorting is disabled to allow manual ordering. Drag the handle icon to reorder rows.',
      },
    },
  },
};

