// Column definitions
const userColumns: ColumnDef<User>[] = [
  {
    accessorKey: 'id',
    header: 'ID',
    meta: {
      sizing: {
        maxWidth: 'xs', // 96px
      }
    } as DsDataTableColumnMeta,
  },
  {
    accessorKey: 'name',
    header: 'Name',
    meta: {
      sizing: {
        minWidth: 'sm',  // 128px
        maxWidth: 'lg',  // 256px
      }
    } as DsDataTableColumnMeta,
  },
  {
    accessorKey: 'email',
    header: 'Email',
    meta: {
      sizing: {
        minWidth: 'md',  // 192px
        maxWidth: 'xl',  // 320px
        truncate: true,
      }
    } as DsDataTableColumnMeta,
  },
  {
    accessorKey: 'role',
    header: 'Role',
    meta: {
      sizing: {
        maxWidth: 'sm', // 128px
      }
    } as DsDataTableColumnMeta,
  },
  {
    accessorKey: 'status',
    header: 'Status',
    meta: {
      sizing: {
        maxWidth: 'sm', // 128px
      }
    } as DsDataTableColumnMeta,
    cell: (info) => {
      const status = info.getValue() as string;
      const variantMap: Record<string, string> = {
        active: 'success',
        inactive: 'grey',
        pending: 'warning',
      };
      const variant = variantMap[status] || 'grey';
      return `<ds-badge variant="${variant}">${status.toUpperCase()}</ds-badge>`;
    },
  },
  {
    accessorKey: 'joinDate',
    header: 'Join Date',
    meta: {
      sizing: {
        maxWidth: 'sm', // 128px
      }
    } as DsDataTableColumnMeta,
  },
];

const productColumns: ColumnDef<Product>[] = [
  {
    accessorKey: 'id',
    header: 'ID',
    meta: {
      sizing: {
        maxWidth: 'xs', // 96px
      }
    } as DsDataTableColumnMeta,
  },
  {
    accessorKey: 'name',
    header: 'Product Name',
    meta: {
      sizing: {
        minWidth: 'md',  // 192px
        maxWidth: 'xl',  // 320px
        truncate: true,
      }
    } as DsDataTableColumnMeta,
  },
  {
    accessorKey: 'category',
    header: 'Category',
    meta: {
      sizing: {
        maxWidth: 'md', // 192px
      }
    } as DsDataTableColumnMeta,
  },
  {
    accessorKey: 'price',
    header: 'Price',
    meta: {
      sizing: {
        maxWidth: 'sm', // 128px
      }
    } as DsDataTableColumnMeta,
    cell: (info) => {
      const price = info.getValue() as number;
      return `<span style="font-weight: 600;">$${price.toFixed(2)}</span>`;
    },
  },
  {
    accessorKey: 'stock',
    header: 'Stock',
    meta: {
      sizing: {
        maxWidth: 'xs', // 96px
      }
    } as DsDataTableColumnMeta,
    cell: (info) => {
      const stock = info.getValue() as number;
      const color = stock === 0 ? '#ef4444' : stock < 10 ? '#f59e0b' : '#10b981';
      return `<span style="color: ${color}; font-weight: 600;">${stock}</span>`;
    },
  },
  {
    accessorKey: 'status',
    header: 'Status',
    meta: {
      sizing: {
        maxWidth: 'md', // 192px
      }
    } as DsDataTableColumnMeta,
    cell: (info) => {
      const status = info.getValue() as string;
      const variantMap: Record<string, string> = {
        in_stock: 'success',
        low_stock: 'warning',
        out_of_stock: 'destructive',
      };
      const variant = variantMap[status] || 'grey';
      const label = status.replace('_', ' ').toUpperCase();
      return `<ds-badge variant="${variant}">${label}</ds-badge>`;
    },
  },
];

