.app-shell-demo {
  display: flex;
  height: 600px;
  background: #f8f9fa;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

:host(.tw-h-screen) .app-shell-demo {
  height: 100vh;
  border-radius: 0;
  box-shadow: none;
}

.demo-sidebar {
  flex-shrink: 0;
  transition: width 0.3s ease;
}

.demo-main {
  flex: 1;
  display: flex;
  flex-direction: column;
  min-width: 0;
}

.demo-topbar {
  flex-shrink: 0;
}

.demo-content {
  flex: 1;
  overflow: auto;
  background: white;
}

.demo-content-inner {
  padding: 24px;
}

.demo-title {
  margin: 0 0 8px 0;
  font-size: 24px;
  font-weight: 600;
  color: #1a1a1a;
}

.demo-description {
  margin: 0 0 24px 0;
  color: #666;
  line-height: 1.5;
}

.demo-metrics {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
  margin-bottom: 24px;
  padding: 16px;
  background: #f8f9fa;
  border-radius: 6px;
}

.demo-metric {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.demo-metric-label {
  font-size: 12px;
  font-weight: 500;
  color: #666;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.demo-metric-value {
  font-size: 14px;
  font-weight: 500;
  color: #1a1a1a;
}

.demo-actions {
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
}

.demo-button {
  padding: 8px 16px;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s;
}

.demo-button:hover {
  background: #0056b3;
}

.demo-button:active {
  background: #004085;
}

.full-page-back-button {
  position: absolute;
  top: 16px;
  left: 16px;
  z-index: 1000;
}

.back-button {
  padding: 8px 16px;
  background: rgba(0, 0, 0, 0.8);
  color: white;
  border: none;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  backdrop-filter: blur(4px);
}

.back-button:hover {
  background: rgba(0, 0, 0, 0.9);
  transform: translateY(-1px);
}

.back-button:active {
  transform: translateY(0);
}

