import { Meta, StoryObj, moduleMetadata } from '@storybook/angular';
import { DsDrawerComponent } from './ds-drawer';
import { DsButtonComponent } from '../button/ds-button';
import { NgpDialogTrigger, NgpDialogOverlay, NgpDialog } from 'ng-primitives/dialog';

interface DrawerStoryProps {
  position: 'left' | 'right';
}

const meta: Meta<DrawerStoryProps> = {
  title: 'Primitives/Drawer',
  component: DsDrawerComponent,
  tags: ['autodocs'],
  decorators: [
    moduleMetadata({
      imports: [DsDrawerComponent, DsButtonComponent, NgpDialogTrigger, NgpDialogOverlay, NgpDialog],
    }),
  ],
  argTypes: {
    position: {
      control: 'select',
      options: ['left', 'right'],
      description: 'Position of the drawer',
      defaultValue: 'right'
    }
  }
};

export default meta;
type Story = StoryObj<DrawerStoryProps>;

const Template: Story['render'] = (args) => ({
  props: args,
  template: `
    <div style="padding: 20px;">
      <ds-button [ngpDialogTrigger]="drawer" variant="primary">Open Drawer</ds-button>
      <p>This is the main page content. When you open the drawer, it should overlay this content with a semi-transparent background.</p>
    </div>

    <ng-template #drawer let-close="close">
      <div ngpDialogOverlay class="ds-drawer-overlay">
        <ds-drawer ngpDialog [position]="position">
          <h2 slot="header" class="tw-text-xl tw-font-semibold">Drawer Title</h2>
          <div slot="content">
            <p class="tw-text-neutral-700 tw-mb-4">This is the main content of the drawer.</p>
            <p class="tw-text-neutral-600 tw-mb-4">You can put any content here including forms, lists, or other components.</p>
            <div class="tw-p-4 tw-bg-gray-50 tw-rounded-lg">
              <h3 class="tw-font-medium tw-mb-2">Example Content</h3>
              <p class="tw-text-sm tw-text-gray-600">This drawer should slide in from the {{ position }} side of the screen.</p>
            </div>
          </div>
          <div slot="footer">
            <ds-button variant="ghost" (click)="close()">Cancel</ds-button>
            <ds-button variant="primary" (click)="close()">Save Changes</ds-button>
          </div>
        </ds-drawer>
      </div>
    </ng-template>
  `
});

export const RightDrawer: Story = {
  args: {
    position: 'right'
  },
  render: Template
};

export const LeftDrawer: Story = {
  args: {
    position: 'left'
  },
  render: Template
};

import { Meta, StoryObj, moduleMetadata, componentWrapperDecorator } from '@storybook/angular';
import { FormsModule } from '@angular/forms';
import { DsSelectComponent, DsSelectOption } from './ds-select';

interface ExampleOption {
  id: string;
  name: string;
}

const meta: Meta<DsSelectComponent> = {
  title: 'Primitives/Select',
  component: DsSelectComponent,
  decorators: [
    moduleMetadata({
      imports: [FormsModule],
    }),
    componentWrapperDecorator(
      (story) => `<div style="display: flex; justify-content: center;"><div style="width: 200px;">${story}</div></div>`
    )
  ],
  tags: ['autodocs'],
  parameters: {
    layout: 'centered',
    docs: {
      story: {
        inline: true,
        iframeHeight: 100,
      }
    }
  },
  argTypes: {
    variant: {
      control: 'select',
      options: ['default', 'error', 'warning', 'success'],
    },
    disabled: {
      control: 'boolean',
    },
    required: {
      control: 'boolean',
    },
  },
};

export default meta;
type Story = StoryObj<DsSelectComponent>;

const simpleOptions: DsSelectOption[] = [
  { id: '1', label: 'Option 1', value: 1 },
  { id: '2', label: 'Option 2', value: 2 },
  { id: '3', label: 'Option 3', value: 3 },
  { id: '4', label: 'Option 4', value: 4 },
];

const groupedOptions: DsSelectOption[] = [
  { id: '1', label: 'Apple', value: 'apple', group: 'Fruits' },
  { id: '2', label: 'Banana', value: 'banana', group: 'Fruits' },
  { id: '3', label: 'Orange', value: 'orange', group: 'Fruits' },
  { id: '4', label: 'Carrot', value: 'carrot', group: 'Vegetables' },
  { id: '5', label: 'Broccoli', value: 'broccoli', group: 'Vegetables' },
  { id: '6', label: 'Potato', value: 'potato', group: 'Vegetables' },
];

export const Default: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    variant: 'default',
    disabled: false,
    required: false,
  },
};

export const WithGroups: Story = {
  args: {
    options: groupedOptions,
    placeholder: 'Select food',
    variant: 'default',
  },
};

export const Disabled: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    disabled: true,
  },
};

export const Error: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    variant: 'error',
  },
};

export const Warning: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    variant: 'warning',
  },
};

export const Success: Story = {
  args: {
    options: simpleOptions,
    placeholder: 'Select an option',
    variant: 'success',
  },
};

export const WithDisabledOptions: Story = {
  args: {
    options: [
      { id: '1', label: 'Option 1', value: 1 },
      { id: '2', label: 'Option 2', value: 2, disabled: true },
      { id: '3', label: 'Option 3', value: 3 },
      { id: '4', label: 'Option 4', value: 4, disabled: true },
    ],
    placeholder: 'Select an option',
  },
};

import { Meta, StoryObj, applicationConfig } from '@storybook/angular';
import { DsDataTableComponent } from './ds-data-table';
import { provideIcons } from '@ng-icons/core';
import { 
  remixSearchLine,
  remixLayoutColumnLine,
  remixCloseLine,
  remixArrowUpLine,
  remixArrowDownLine,
  remixArrowUpDownLine,
  remixInboxLine,
  remixArrowLeftSLine,
  remixArrowRightSLine,
  remixEyeLine,
  remixEditLine,
  remixDeleteBinLine,
  remixLoader4Line,
} from '@ng-icons/remixicon';
import type { ColumnDef } from '@tanstack/angular-table';
import { signal } from '@angular/core';

// Sample data types
interface User {
  id: number;
  name: string;
  email: string;
  role: string;
  status: 'active' | 'inactive' | 'pending';
  joinDate: string;
}

interface Product {
  id: number;
  name: string;
  category: string;
  price: number;
  stock: number;
  status: 'in_stock' | 'low_stock' | 'out_of_stock';
}

// Sample data
const sampleUsers: User[] = [
  { id: 1, name: 'John Doe', email: 'john.doe@example.com', role: 'Admin', status: 'active', joinDate: '2023-01-15' },
  { id: 2, name: 'Jane Smith', email: 'jane.smith@example.com', role: 'Editor', status: 'active', joinDate: '2023-02-20' },
  { id: 3, name: 'Bob Johnson', email: 'bob.johnson@example.com', role: 'Viewer', status: 'inactive', joinDate: '2023-03-10' },
  { id: 4, name: 'Alice Williams', email: 'alice.w@example.com', role: 'Admin', status: 'active', joinDate: '2023-04-05' },
  { id: 5, name: 'Charlie Brown', email: 'charlie.b@example.com', role: 'Editor', status: 'pending', joinDate: '2023-05-12' },
  { id: 6, name: 'Diana Prince', email: 'diana.p@example.com', role: 'Viewer', status: 'active', joinDate: '2023-06-18' },
  { id: 7, name: 'Ethan Hunt', email: 'ethan.h@example.com', role: 'Editor', status: 'active', joinDate: '2023-07-22' },
  { id: 8, name: 'Fiona Green', email: 'fiona.g@example.com', role: 'Viewer', status: 'inactive', joinDate: '2023-08-30' },
  { id: 9, name: 'George Miller', email: 'george.m@example.com', role: 'Admin', status: 'active', joinDate: '2023-09-14' },
  { id: 10, name: 'Hannah Montana', email: 'hannah.m@example.com', role: 'Editor', status: 'active', joinDate: '2023-10-01' },
];

const sampleProducts: Product[] = [
  { id: 1, name: 'Laptop Pro', category: 'Electronics', price: 1299.99, stock: 45, status: 'in_stock' },
  { id: 2, name: 'Wireless Mouse', category: 'Accessories', price: 29.99, stock: 150, status: 'in_stock' },
  { id: 3, name: 'USB-C Cable', category: 'Accessories', price: 12.99, stock: 8, status: 'low_stock' },
  { id: 4, name: 'Monitor 27"', category: 'Electronics', price: 399.99, stock: 0, status: 'out_of_stock' },
  { id: 5, name: 'Keyboard Mechanical', category: 'Accessories', price: 89.99, stock: 75, status: 'in_stock' },
  { id: 6, name: 'Webcam HD', category: 'Electronics', price: 79.99, stock: 5, status: 'low_stock' },
  { id: 7, name: 'Desk Lamp', category: 'Office', price: 34.99, stock: 120, status: 'in_stock' },
  { id: 8, name: 'Office Chair', category: 'Furniture', price: 249.99, stock: 25, status: 'in_stock' },
];

// Column definitions
const userColumns: ColumnDef<User>[] = [
  {
    accessorKey: 'id',
    header: 'ID',
    size: 80,
  },
  {
    accessorKey: 'name',
    header: 'Name',
    size: 200,
  },
  {
    accessorKey: 'email',
    header: 'Email',
    size: 250,
  },
  {
    accessorKey: 'role',
    header: 'Role',
    size: 120,
  },
  {
    accessorKey: 'status',
    header: 'Status',
    size: 120,
    cell: (info) => {
      const status = info.getValue() as string;
      const statusColors: Record<string, string> = {
        active: 'color: #10b981; background: #d1fae5; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;',
        inactive: 'color: #6b7280; background: #f3f4f6; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;',
        pending: 'color: #f59e0b; background: #fef3c7; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;',
      };
      return `<span style="${statusColors[status] || statusColors['inactive']}">${status.toUpperCase()}</span>`;
    },
  },
  {
    accessorKey: 'joinDate',
    header: 'Join Date',
    size: 130,
  },
];

const productColumns: ColumnDef<Product>[] = [
  {
    accessorKey: 'id',
    header: 'ID',
    size: 80,
  },
  {
    accessorKey: 'name',
    header: 'Product Name',
    size: 200,
  },
  {
    accessorKey: 'category',
    header: 'Category',
    size: 150,
  },
  {
    accessorKey: 'price',
    header: 'Price',
    size: 120,
    cell: (info) => {
      const price = info.getValue() as number;
      return `<span style="font-weight: 600;">$${price.toFixed(2)}</span>`;
    },
  },
  {
    accessorKey: 'stock',
    header: 'Stock',
    size: 100,
    cell: (info) => {
      const stock = info.getValue() as number;
      const color = stock === 0 ? '#ef4444' : stock < 10 ? '#f59e0b' : '#10b981';
      return `<span style="color: ${color}; font-weight: 600;">${stock}</span>`;
    },
  },
  {
    accessorKey: 'status',
    header: 'Status',
    size: 130,
    cell: (info) => {
      const status = info.getValue() as string;
      const statusColors: Record<string, string> = {
        in_stock: 'color: #10b981; background: #d1fae5; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;',
        low_stock: 'color: #f59e0b; background: #fef3c7; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;',
        out_of_stock: 'color: #ef4444; background: #fee2e2; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;',
      };
      return `<span style="${statusColors[status]}">${status.replace('_', ' ').toUpperCase()}</span>`;
    },
  },
];

// Columns with actions
const userColumnsWithActions: ColumnDef<User>[] = [
  ...userColumns,
  {
    id: 'actions',
    header: 'Actions',
    enableSorting: false,
    enableHiding: false,
    size: 120,
    cell: (info) => {
      return `
        <div style="display: flex; gap: 8px; align-items: center;">
          <button 
            class="action-btn"
            style="padding: 4px 8px; border: none; background: transparent; cursor: pointer; border-radius: 4px; color: var(--text-color-default-secondary); transition: all 0.2s;"
            onmouseover="this.style.background='var(--background-color-interactive-default-hover)'; this.style.color='var(--text-color-default-primary)';"
            onmouseout="this.style.background='transparent'; this.style.color='var(--text-color-default-secondary)';"
            onclick="alert('Edit user: ${info.row.original.name}')"
            title="Edit">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
              <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"></path>
              <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"></path>
            </svg>
          </button>
          <button 
            class="action-btn"
            style="padding: 4px 8px; border: none; background: transparent; cursor: pointer; border-radius: 4px; color: var(--text-color-default-secondary); transition: all 0.2s;"
            onmouseover="this.style.background='#fee2e2'; this.style.color='#ef4444';"
            onmouseout="this.style.background='transparent'; this.style.color='var(--text-color-default-secondary)';"
            onclick="alert('Delete user: ${info.row.original.name}')"
            title="Delete">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
              <polyline points="3 6 5 6 21 6"></polyline>
              <path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path>
            </svg>
          </button>
        </div>
      `;
    },
  },
];

const meta: Meta<DsDataTableComponent> = {
  title: 'Components/Data Table',
  component: DsDataTableComponent,
  decorators: [
    applicationConfig({
      providers: [
        provideIcons({
          remixSearchLine,
          remixLayoutColumnLine,
          remixCloseLine,
          remixArrowUpLine,
          remixArrowDownLine,
          remixArrowUpDownLine,
          remixInboxLine,
          remixArrowLeftSLine,
          remixArrowRightSLine,
          remixEyeLine,
          remixEditLine,
          remixDeleteBinLine,
          remixLoader4Line,
        }),
      ],
    }),
  ],
  tags: ['autodocs'],
  argTypes: {
    data: {
      control: false,
      description: 'Array of data to display in the table',
    },
    columns: {
      control: false,
      description: 'Column definitions for the table',
    },
    searchable: {
      control: 'boolean',
      description: 'Enable global search functionality',
    },
    searchPlaceholder: {
      control: 'text',
      description: 'Placeholder text for search input',
    },
    paginated: {
      control: 'boolean',
      description: 'Enable pagination',
    },
    pageSize: {
      control: 'number',
      description: 'Number of rows per page',
    },
    showColumnVisibility: {
      control: 'boolean',
      description: 'Show column visibility toggle',
    },
    emptyMessage: {
      control: 'text',
      description: 'Message to display when table is empty',
    },
    rowClickable: {
      control: 'boolean',
      description: 'Make rows clickable',
    },
  },
};

export default meta;
type Story = StoryObj<DsDataTableComponent>;

export const Default: Story = {
  args: {
    data: sampleUsers,
    columns: userColumns,
    searchable: true,
    paginated: true,
    pageSize: 5,
    showColumnVisibility: true,
  },
};

export const Products: Story = {
  args: {
    data: sampleProducts,
    columns: productColumns,
    searchable: true,
    searchPlaceholder: 'Search products...',
    paginated: true,
    pageSize: 5,
    showColumnVisibility: true,
  },
};

export const WithActions: Story = {
  args: {
    data: sampleUsers,
    columns: userColumnsWithActions,
    searchable: true,
    paginated: true,
    pageSize: 5,
    showColumnVisibility: true,
    rowClickable: false,
  },
};

export const WithoutSearch: Story = {
  args: {
    data: sampleUsers,
    columns: userColumns,
    searchable: false,
    paginated: true,
    pageSize: 5,
    showColumnVisibility: true,
  },
};

export const WithoutPagination: Story = {
  args: {
    data: sampleUsers.slice(0, 5),
    columns: userColumns,
    searchable: true,
    paginated: false,
    showColumnVisibility: true,
  },
};

export const LargeDataset: Story = {
  args: {
    data: Array.from({ length: 100 }, (_, i) => ({
      id: i + 1,
      name: `User ${i + 1}`,
      email: `user${i + 1}@example.com`,
      role: ['Admin', 'Editor', 'Viewer'][i % 3],
      status: (['active', 'inactive', 'pending'] as const)[i % 3],
      joinDate: new Date(2023, i % 12, (i % 28) + 1).toISOString().split('T')[0],
    })),
    columns: userColumns,
    searchable: true,
    paginated: true,
    pageSize: 10,
    pageSizeOptions: [10, 25, 50, 100],
    showColumnVisibility: true,
  },
};

export const EmptyState: Story = {
  args: {
    data: [],
    columns: userColumns,
    searchable: true,
    paginated: true,
    pageSize: 10,
    emptyMessage: 'No users found',
  },
};

export const ClickableRows: Story = {
  args: {
    data: sampleUsers,
    columns: userColumns,
    searchable: true,
    paginated: true,
    pageSize: 5,
    rowClickable: true,
  },
  render: (args) => ({
    props: {
      ...args,
      onRowClicked: (row: User) => {
        alert(`Clicked on: ${row.name}`);
      },
    },
    template: `
      <ds-data-table
        [data]="data"
        [columns]="columns"
        [searchable]="searchable"
        [paginated]="paginated"
        [pageSize]="pageSize"
        [rowClickable]="rowClickable"
        (rowClicked)="onRowClicked($event)"
      />
    `,
  }),
};

export const CompactView: Story = {
  args: {
    data: sampleProducts,
    columns: productColumns,
    searchable: true,
    paginated: true,
    pageSize: 10,
    showColumnVisibility: false,
  },
};

