    accessorKey: 'price',
    header: 'Price',
    size: 120,
    cell: (info) => {
      const price = info.getValue() as number;
      return `<span style="font-weight: 600;">$${price.toFixed(2)}</span>`;
    },
  },
  {
    accessorKey: 'stock',
    header: 'Stock',
    size: 100,
    cell: (info) => {
      const stock = info.getValue() as number;
      const color = stock === 0 ? '#ef4444' : stock < 10 ? '#f59e0b' : '#10b981';
      return `<span style="color: ${color}; font-weight: 600;">${stock}</span>`;
    },
  },
  {
    accessorKey: 'status',
    header: 'Status',
    size: 130,
    cell: (info) => {
      const status = info.getValue() as string;
      const statusColors: Record<string, string> = {
        in_stock: 'color: #10b981; background: #d1fae5; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;',
        low_stock: 'color: #f59e0b; background: #fef3c7; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;',
        out_of_stock: 'color: #ef4444; background: #fee2e2; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;',
      };
      return `<span style="${statusColors[status]}">${status.replace('_', ' ').toUpperCase()}</span>`;
    },
  },
];

